mapper.select:
SELECT imei,w.emp_name as peopleName,w.id as peopleId, (SELECT construction_name from hj_construction_company c WHERE a.bid=c.id) as constructionName, a.`name` as areaName,a.id as areaId from sb_hire h LEFT JOIN sb_area a ON(h.areaId=a.id) LEFT JOIN hj_project_workers w ON(h.peopleId = w.id) WHERE 1=1
AND emp_name like "%${userName}%"
AND a.pid=#{projectId}

select u.id as id,u.emp_name as userName,u.emp_phon as userPhone, (select construction_name from hj_construction_company c where a.bid=c.id) as constructionName, r.imei as imei,a.`name` as areaName from sb_hire h LEFT JOIN hj_project_workers u ON(h.peopleId=u.id) LEFT JOIN sb_area a ON (h.areaId=a.id) LEFT JOIN sb_equipment_record r ON(h.imei=r.imei) where 1=1
AND emp_name = #{userName}
AND a.pid=#{projectId}
GROUP BY h.peopleId

select r.bat as bat,r.watchDate as watchDate,r.address as address from sb_equipment_record r where 1=1
AND r.imei = #{imei}
AND watchDate like "${watchDate}%" ORDER BY watchDate LIMIT 0,1

select r.bat as bat,r.watchDate as watchDate,r.address as address from sb_equipment_record r where 1=1
AND r.imei = #{imei}
AND watchDate like "${watchDate}%" ORDER BY watchDate desc LIMIT 0,1

select r.xloc as xloc,r.yloc as yloc,r.watchDate as watchDate from sb_equipment_record r where 1=1
AND r.imei = #{imei}
AND watchDate like "${watchDate}%" ORDER BY watchDate

SELECT u.id AS id, u.emp_name AS userName, u.emp_phon AS userPhone, u.id_code AS idCode, u.start_time AS startTime, emp_naticeplace AS empNaticeplace, (SELECT title FROM hj_dictionaries e WHERE e.tag = u.job_name) as workType, (SELECT construction_name FROM hj_construction_company c WHERE a.bid = c.id ) AS constructionName, r.imei AS imei, a.`name` as areaName, a.id as areaId, a.xloc as areaXloc, a.yloc as areaYloc, a.radius as areaRadius, a.way FROM sb_hire h LEFT JOIN hj_project_workers u ON(h.peopleId=u.id) LEFT JOIN sb_area a ON (h.areaId=a.id) LEFT JOIN sb_equipment_record r ON(h.imei=r.imei) WHERE 1=1
AND `emp_name`=#{filed} or r.imei=#{filed} or `emp_phon` = #{filed}
AND a.pid=#{projectId}
GROUP BY h.peopleId

        select h.imei,u.id as id,u.emp_name as userName,u.emp_phon as userPhone,
        (select construction_name from hj_construction_company c where a.bid=c.id) as constructionName,
        a.xloc as areaXloc,a.yloc as areaYloc,a.radius as areaRadius,a.name as areaName
        from sb_hire h
        LEFT JOIN hj_project_workers u ON(h.peopleId=u.id)
        LEFT JOIN sb_area a ON (h.areaId=a.id)
        LEFT JOIN sb_equipment_record r ON(h.imei=r.imei)
        where 1=1
        
            AND `emp_name`=#{filed}
            
                AND a.pid=#{projectId}
            
            or r.imei=#{filed}
            
                AND a.pid=#{projectId}
            
            or `emp_phon` = #{filed}
            
                AND a.pid=#{projectId}
            
        
        
            AND a.pid=#{projectId}
        
			GROUP BY h.peopleId

SELECT r.imei AS imei, r.bat AS bat, r.watchDate AS watchDate, r.xloc AS xloc, r.yloc AS yloc, r.address AS address FROM sb_equipment_record r WHERE 1 = 1
AND r.imei = #{imei}
and `watchDate` like "${startTime}%" ORDER BY watchDate DESC

SELECT r.imei AS imei, r.bat AS bat, r.watchDate AS watchDate, r.xloc AS xloc, r.yloc AS yloc, r.address AS address FROM sb_equipment_record r WHERE 1 = 1
AND r.imei = #{imei}
ORDER BY watchDate DESC

mapper.update:
update sb_hire
imei = #{imei}, peopleId = #{peopleId}, areaId = #{areaId},
where id = #{id}
